<html><script src="http://code.jquery.com/jquery-1.9.1.js"></script>    <input type="number" value="1" placeholder="device#" id="device_id"><br>    <input type="number" value="10" placeholder="total soldiers" id="soldiers"><br>    <button onclick="startWar()">Lets GO!</button>    <script>        function refresh_soldiers(device_id, callback)        {            $.get("<?=base_url()."admin/refresh/"?>"+device_id, function(data,status){                if(data == 1)                    callback();            });        }        function startWar()        {            var device_id = document.getElementById("device_id").value;            refresh_soldiers(device_id, function(){                openTabs();            });        }        function openTabs()        {            var soldiers = document.getElementById("soldiers").value;            for(var i = 0; i < soldiers; i++)                openNewBackgroundTab();        }        function openNewBackgroundTab(){            var device_id = document.getElementById("device_id").value;            var a = document.createElement("a");            a.href = "http://localhost/production/projectz2/admin/war_zone/"+device_id;            var evt = document.createEvent("MouseEvents");            //the tenth parameter of initMouseEvent sets ctrl key            evt.initMouseEvent("click", true, true, window, 0, 0, 0, 0, 0,                true, false, false, false, 0, null);            a.dispatchEvent(evt);        }    </script></html>